import { createStore } from "redux";
import { createSlice } from "redux";

//import { createReducer } from 'redux';
// there are two things that you can import
// createSlice & create reducer that is provided by
// redux toolkit to make coding easy and managebel
// npm install @reduxjs/toolkit

const intialValue = { counter: 0, showCounter: true };

createSlice({
  name: "counter", // name could be anything - slice name
  initialState: intialValue,
  reducer: {                      // we have used four condition that's why we need
    increment(state) {                // four function in reducer -
      state.counter++;
    },
    decrement(state) {
      state.counter--;
    }, 
    increase(state, action) {
      state.counter = state.counter + action.amount; 
    }, 
    toggleCounter(state) {
      state.showCounter = !state.showCounter;
    },
  },
});

